import axios from "axios";

//url de l'endpoint de l'api
const API_URL = process.env.REACT_APP_BASE_URL;
//verifier s'il existe un token dans les localstorage
const token = localStorage.getItem('token')

//ajouter le header a la requete - preciser le type des donnee
const headersFiles = {
    headers: {
        'Accept': 'application/json',
        'Authorization': 'Bearer ' + token
    },
};

//fonction pour executer l'api login 
const getAll = () => {
    return axios.get(API_URL + "/api/posts", headersFiles);
};

//fonction pour executer l'api login 
const create = (data) => {
    return axios.post(API_URL + "/api/posts", data, headersFiles);
};

// //fonction pour executer l'api login 
// const update = (data) => {
//     return axios.post(API_URL + "/api/auth/signup", data, headersFiles);
// };

// //fonction pour executer l'api login 
// const delete = (data) => {
//     return axios.post(API_URL + "/api/auth/signup", data, headersFiles);
// };

const Post = {
    getAll,
    create
}
export default Post;
