import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../assets/css/globale.scss";
import logo from "../assets/logo/icon-left-font-monochrome-white.png";
import Header from "../components/Header";
import User from "../services/user.services";
function Login() {
	//declarer les states : variable + fonction pour gerer variable de type tableau
	const [values, setValues] = useState([]);
	// creer un variable pour ajouter les erreurs
	const [error, setError] = useState([]);
	//creer un variable pour ajouter les message de confirmation
	const [success, setSuccess] = useState([]);

	// variable de type navigation pour ajouter des liens entre les pages (sans a href)
	const navigate = useNavigate();

	//handlechange : detecter les changements des inputs et ajouter la valeur dans le variable values
	const handleChange = event => {
		setValues(values => ({
			// pour changer la valeur du variable values
			...values, // pour ajouter la variable au tableau (sans supprimé l'existant)
			[event.target.name]: event.target.value // la valeur a ajouter : name champ: valeur champ
		}));
	};

	const handleSubmit = () => {
		// fonction pour envoyer le formulaire
		User.login(values) // appel pour la fonction login dans le fichier service
			.then(val => {
				// if succes : appel api reussi
				setSuccess("Connexion réussi"); // ajouter valeur au variable success
				//enregistrer token, user id, role dans les localstorage
				localStorage.setItem("userId", val.data.userId);
				localStorage.setItem("token", val.data.token);
				localStorage.setItem("role", val.data.role);
				// redirection vers la page d'accueil
				navigate("/");
			})
			.catch(err => {
				// erreur de requete
				setError(err.response.data); // ajouter une valeur au variable error
				console.log(err.response.data); // afficher l'erreur dans le console
			});
	};

	return (
		<div className="App">
			<Header />

			<div className="form">
				<h2>Se connecter</h2>
				<label className="error">{error}</label>
				<div className="inputgroup">
					<label>Login</label>
					<input type="email" name="email" onChange={handleChange}></input>
				</div>
				<div className="inputgroup">
					<label>Password</label>
					<input type="password" name="password" onChange={handleChange}></input>
				</div>
				<div className="inputgroup">
					<button onClick={handleSubmit}>Login</button>
				</div>
			</div>

			<footer>
				<img src={logo} />
			</footer>
		</div>
	);
}

export default Login;
